const { createCanvas } = require('canvas');

var CANVAS_WIDTH = 10;
var CANVAS_HEIGHT = 10;

var canvas = createCanvas(CANVAS_WIDTH, CANVAS_HEIGHT);
var context = canvas.getContext('2d');
context.antialias = 'none';
canvas.width = CANVAS_WIDTH;
canvas.height = CANVAS_HEIGHT;

var FPS = 60;

var context;
var dot;

init();

function init() {



createTrail();

setInterval(loop, 1000 / FPS);

}

function createTrail() {
  dot = {
    x: CANVAS_WIDTH, 
    y: CANVAS_HEIGHT,
    speed: 0.3,
    direction: Math.PI * 2 * Math.random()
  }
}

function updatePosition() {
  var dx = dot.x + dot.speed * Math.cos(dot.direction);
  var dy = dot.y + dot.speed * Math.sin(dot.direction);

  if (dx < 0 || dx > CANVAS_WIDTH || dy < 0 || dy > CANVAS_HEIGHT) {
    dot.direction = Math.PI * 2 * Math.random();
    updatePosition();
  } else {
    dot.x = dx;
    dot.y = dy;
  }
}

function loop() {
  updatePosition();

  // Draw over the whole canvas to create the trail effect
  context.fillStyle = 'rgba(255, 255, 255, 0.01)';
  context.fillRect(0, 0, canvas.width, canvas.height);

  // Draw the dot
  context.beginPath();
  context.fillStyle = '#ff0000';
  context.moveTo(dot.x, dot.y);
  context.arc(dot.x, dot.y, 1, 0, Math.PI*2, true);
  context.fill();

  msg.mode = 'canvas';
  msg.payload = canvas.toDataURL();
  node.send(msg);
}

if (msg.payload === false) {
    if (context.timeout) {
        clearTimeout(context.timeout);
        return;
    }
}

var requestAnimFrame = (function(){
  return function( callback ){
			context.timeout = setTimeout(callback, 1000 / 60);
		  };
})();

const { createCanvas, loadImage } = require('canvas');

var W = 64;//window.innerWidth
var H = 64;//window.innerHeight

const canvas = createCanvas(W, H);
var ctx = canvas.getContext("2d");
ctx.antialias = 'none';
var circles = [];

canvas.width = W;
canvas.height = H; 

//Random Circles creator
function create() {

	//Place the circles at the center

	this.x = W/2;
	this.y = H/2;


	//Random radius between 2 and 6
	this.radius = 1 + Math.random()*1; 

	//Random velocities
	this.vx = -0.5 + Math.random()*2;
	this.vy = -0.5 + Math.random()*2;

	//Random colors
	this.r = Math.round(Math.random())*255;
	this.g = Math.round(Math.random())*127;
	this.b = Math.round(Math.random())*55;
}

for (var i = 0; i < 10; i++) {
	circles.push(new create());
}

function draw() {

	//Fill canvas with black color
    ctx.globalCompositeOperation = "source-over";
    ctx.fillStyle = "rgba(0,0,0,0.15)";
    ctx.fillRect(0, 0, W, H);

	//Fill the canvas with circles
	for(var j = 0; j < circles.length; j++){
		var c = circles[j];

		//Create the circles
		ctx.beginPath();
		ctx.arc(c.x, c.y, c.radius, 0, Math.PI*2, false);
    ctx.fillStyle = "rgba("+c.r+", "+c.g+", "+c.b+", 0.5)";
		ctx.fill();

		c.x += c.vx;
		c.y += c.vy;
		c.radius -= .02;

		if(c.radius < 0)
			circles[j] = new create();
	}

	msg.mode = 'canvas';
	msg.payload = canvas.toDataURL();
	node.send(msg);
}

function animate() {
	requestAnimFrame(animate);
	draw();
}

animate();
